import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';
import Matter from 'matter-js';

const WhiteboardContainer = styled.div`
  position: relative;
  width: 100%;
  height: calc(100vh - 100px);
  background: white;
  overflow: hidden;
  cursor: default;

  &.dragging {
    cursor: grabbing;
  }
`;

const Canvas = styled.canvas`
  display: block;
  width: 100%;
  height: 100%;
  border: 1px solid #ccc;
`;

interface Thought {
  id: number;
  text: string;
  color: string;
  size: 'small' | 'medium' | 'large';
  isPinned: boolean;
}

interface WhiteboardProps {
  thoughts: Thought[];
}

const Whiteboard: React.FC<WhiteboardProps> = ({ thoughts }) => {
  const containerRef = useRef<HTMLDivElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const engineRef = useRef<Matter.Engine | null>(null);
  const renderRef = useRef<Matter.Render | null>(null);
  const bodiesRef = useRef<{ [key: number]: Matter.Body }>({});
  const mouseConstraintRef = useRef<Matter.MouseConstraint | null>(null);
  const isDraggingRef = useRef(false);

  useEffect(() => {
    if (!canvasRef.current || !containerRef.current) return;

    console.log('Initializing Matter.js engine');

    // Get container dimensions
    const container = containerRef.current;
    const width = container.clientWidth;
    const height = container.clientHeight;

    console.log('Container dimensions:', { width, height });

    // Create Matter.js engine
    const engine = Matter.Engine.create({
      gravity: { x: 0, y: 0 }
    });
    engineRef.current = engine;

    // Create renderer with correct type
    const render = Matter.Render.create({
      element: container,
      canvas: canvasRef.current,
      engine: engine,
      options: {
        width,
        height,
        wireframes: false,
        showVelocity: false,
        showCollisions: false,
        background: 'white',
        pixelRatio: window.devicePixelRatio
      }
    });
    renderRef.current = render;

    // Add walls to contain thoughts
    const wallOptions = { 
      isStatic: true,
      render: { visible: false }
    };

    const wallThickness = 60;
    const walls = [
      Matter.Bodies.rectangle(0, height/2, wallThickness, height, wallOptions),
      Matter.Bodies.rectangle(width, height/2, wallThickness, height, wallOptions),
      Matter.Bodies.rectangle(width/2, 0, width, wallThickness, wallOptions),
      Matter.Bodies.rectangle(width/2, height, width, wallThickness, wallOptions)
    ];

    Matter.World.add(engine.world, walls);

    // Set up rendering loop
    let animationFrameId: number;

    const renderScene = () => {
      const ctx = render.canvas.getContext('2d');
      if (!ctx) return;

      // Clear the canvas
      ctx.clearRect(0, 0, width, height);
      ctx.fillStyle = 'white';
      ctx.fillRect(0, 0, width, height);

      // Draw all thoughts
      Object.values(bodiesRef.current).forEach((body) => {
        const thought = thoughts.find(t => t.id === body.id);
        if (!thought) return;

        const pos = body.position;
        ctx.save();
        ctx.translate(pos.x, pos.y);
        ctx.rotate(body.angle);

        // Draw text with subtle shadow
        ctx.shadowColor = 'rgba(0, 0, 0, 0.1)';
        ctx.shadowBlur = 4;
        ctx.shadowOffsetX = 1;
        ctx.shadowOffsetY = 1;

        ctx.font = '18px -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif';
        ctx.fillStyle = '#333';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';
        ctx.fillText(thought.text, 0, 0);

        ctx.restore();
      });

      animationFrameId = requestAnimationFrame(renderScene);
    };

    // Start the physics simulation
    Matter.Runner.run(Matter.Runner.create(), engine);
    renderScene();

    // Handle window resize
    const handleResize = () => {
      const newWidth = container.clientWidth;
      const newHeight = container.clientHeight;
      render.canvas.width = newWidth;
      render.canvas.height = newHeight;
      Matter.Render.setPixelRatio(render, window.devicePixelRatio);
    };
    window.addEventListener('resize', handleResize);

    // Create mouse constraint
    const mouse = Matter.Mouse.create(render.canvas);
    const mouseConstraint = Matter.MouseConstraint.create(engine, {
      mouse: mouse,
      constraint: {
        stiffness: 0.2,
        damping: 0.1,
        render: {
          visible: false
        }
      }
    });

    // Ensure proper mouse scaling
    const pixelRatio = window.devicePixelRatio;
    mouse.pixelRatio = pixelRatio;

    // Add mouse constraint to world
    mouseConstraintRef.current = mouseConstraint;
    Matter.World.add(engine.world, mouseConstraint);

    // Handle mouse events
    Matter.Events.on(mouseConstraint, 'mousedown', (event) => {
      const mousePosition = event.mouse.position;
      const bodiesAtPoint = Matter.Query.point(Object.values(bodiesRef.current), mousePosition);

      if (bodiesAtPoint.length > 0) {
        render.canvas.style.cursor = 'grabbing';
      }
    });

    Matter.Events.on(mouseConstraint, 'mousemove', (event) => {
      const mousePosition = event.mouse.position;
      const bodiesAtPoint = Matter.Query.point(Object.values(bodiesRef.current), mousePosition);

      render.canvas.style.cursor = bodiesAtPoint.length > 0 ? 'grab' : 'default';
    });

    Matter.Events.on(mouseConstraint, 'mouseup', () => {
      render.canvas.style.cursor = 'default';
    });

    Matter.Events.on(mouseConstraint, 'startdrag', () => {
      render.canvas.style.cursor = 'grabbing';
    });

    Matter.Events.on(mouseConstraint, 'enddrag', () => {
      render.canvas.style.cursor = 'grab';
    });

    return () => {
      window.removeEventListener('resize', handleResize);
      cancelAnimationFrame(animationFrameId);
      Matter.Events.off(mouseConstraint, 'mousedown');
      Matter.Events.off(mouseConstraint, 'mousemove');
      Matter.Events.off(mouseConstraint, 'mouseup');
      Matter.Events.off(mouseConstraint, 'startdrag');
      Matter.Events.off(mouseConstraint, 'enddrag');
      Matter.World.remove(engine.world, mouseConstraint);
      Matter.Engine.clear(engine);
      render.canvas.remove();
    };
  }, [thoughts]);

  // Update physics bodies when thoughts change
  useEffect(() => {
    if (!engineRef.current || !renderRef.current) return;

    console.log('Updating physics bodies for thoughts:', thoughts);
    const engine = engineRef.current;

    // Remove bodies that no longer have corresponding thoughts
    Object.entries(bodiesRef.current).forEach(([id, body]) => {
      if (!thoughts.find(t => t.id === Number(id))) {
        Matter.World.remove(engine.world, body);
        delete bodiesRef.current[Number(id)];
      }
    });

    // Update or create bodies for thoughts
    thoughts.forEach((thought) => {
      if (!bodiesRef.current[thought.id]) {
        console.log('Creating new body for thought:', thought);

        const container = containerRef.current;
        if (!container) return;

        // Calculate random position within visible area
        const margin = 100;
        const x = margin + Math.random() * (container.clientWidth - 2 * margin);
        const y = margin + Math.random() * (container.clientHeight - 2 * margin);

        const body = Matter.Bodies.circle(x, y, 30, {
          id: thought.id,
          render: {
            visible: false
          },
          isStatic: thought.isPinned,
          friction: 0.05,
          frictionAir: 0.02,
          restitution: 0.3,
          mass: 1,
          inertia: Infinity, // Prevent rotation while dragging
          collisionFilter: {
            category: 0x0001,
            mask: 0xFFFFFF
          }
        });

        bodiesRef.current[thought.id] = body;
        Matter.World.add(engine.world, body);

        // Add some initial velocity
        Matter.Body.setVelocity(body, {
          x: (Math.random() - 0.5) * 5,
          y: (Math.random() - 0.5) * 5
        });
      }
    });
  }, [thoughts]);

  return (
    <WhiteboardContainer ref={containerRef}>
      <Canvas ref={canvasRef} />
    </WhiteboardContainer>
  );
};

export default Whiteboard;
