import React, { useState } from 'react';
import styled from 'styled-components';
import Whiteboard from './components/Whiteboard';
import ChatInput from './components/ChatInput';

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  background-color: white;
`;

const WhiteboardContainer = styled.div`
  flex: 1;
  position: relative;
  overflow: hidden;
`;

const ChatContainer = styled.div`
  padding: 1rem;
  background-color: #f8f9fa;
  border-top: 1px solid #e9ecef;
`;

const App: React.FC = () => {
  const [thoughts, setThoughts] = useState<Array<{
    id: number;
    text: string;
    color: string;
    size: 'small' | 'medium' | 'large';
    isPinned: boolean;
  }>>([]);

  const handleThoughtSubmit = (thought: string) => {
    if (thought.startsWith('/')) {
      // Handle commands
      const [command, ...args] = thought.slice(1).split(' ');
      console.log('Command:', command, 'Args:', args);
      return;
    }

    // Add new thought
    const newThought = {
      id: Date.now(),
      text: thought,
      color: '#000000',
      size: 'medium' as const,
      isPinned: false,
    };

    setThoughts(prev => [...prev, newThought]);
  };

  return (
    <AppContainer>
      <WhiteboardContainer>
        <Whiteboard thoughts={thoughts} />
      </WhiteboardContainer>
      <ChatContainer>
        <ChatInput onThoughtSubmit={handleThoughtSubmit} />
      </ChatContainer>
    </AppContainer>
  );
};

export default App;
